export class Films extends Component() {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }
  componentDidMount() {
    fetch("https://ghibliapi.herokuapp.com/films")
      .then(response => response.json)
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        })
      });
  }
