import React, { Component } from 'react';


export class Films extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filmsArr: [],
      isLoaded: false,
    };
  }

  componentDidMount() {
    fetch("https://ghibliapi.herokuapp.com/films")
      .then(response => response.json)
      .then(data => {
        this.setState({
          filmsArr: data,
          isLoaded: true,
        })
      })
        .catch((error) => console.log(error));
  }
  render() {
      return (
        <div className="App">
          {this.state.filmsArr}
        </div>
      )
    }
  }

  
export default Films
